definition.script:
pipeline {
agent any
stages {
stage('Download Artifacts') {
steps {
cleanWs()
script {
def artifactory
def downloadSpec
//Setup Artifactory Connection and Download Specs
echo 'Setting up Artifactory Connection......'
artifactory = Artifactory.newServer url: 'http://18.234.252.228:8081/artifactory', credentialsId: 'artifactory-credentials'
downloadSpec =
"""{
"files": [
{
"pattern": "example-repo-local/snowball_installer.zip",
"target": ""
}  
]
}"""
// download from Artifactory.
artifactory.download downloadSpec
}
}
}
stage('Deployment') {
steps {
sh 'pwd'
sh 'ls -ltr'
echo 'validating Java is installed or not'
sh label: '', script: '''if java -version 2>&1 >/dev/null | egrep "\nS+\ns+version";then
echo "Java installed."
else 
echo "Java NOT installed!"
sleep 5
echo "installing Java"
sudo  apt-get install default-jdk -y
sleep 5
java -version
echo "Java installed"
fi'''
sh 'cd /home/devops/deploy_jarfile && sudo  rm -rf /home/devops/deploy_jarfile/* && pwd && ls -ltr'
sh 'cp /var/lib/jenkins/workspace/java-deployment-pipeline/snowball_installer.zip /home/devops/deploy_jarfile/ && ls -ltr'
sh 'cd /home/devops/deploy_jarfile/  && unzip *.zip  && ls -ltr && rm -rf *.zip  && cd java_artifact && ls -ltr '     
sh 'cd /home/devops/deploy_jarfile/java_artifact/ && nohup java -jar *.jar &'
}
}
}
}
